const shell = require('shelljs');

class WhyWouldYouEverUseThis {
	constructor() {}
	async install() {
		shell.exec(
			require('os').platform() == 'win32'
				? 'curl https://bootstrap.pypa.io/get-pip.py -o get-pip.py && python get-pip.py && python -m pip install --upgrade pip'
				: 'wget https://bootstrap.pypa.io/get-pip.py -o get-pip.py && python get-pip.py && python -m pip install --upgrade pip'
		);
		console.clear();
		return true;
	}
	async exec(command) {
		if (typeof command == 'string') shell.exec(`pip3 ${command}`);
		else throw new TypeError('Expected object to be a string but recieved ' + typeof command);
	}
}

module.exports = new WhyWouldYouEverUseThis();
